mapper.select:
SELECT 

FROM crm_student_praise a

WHERE a.id = #{id}

SELECT 

FROM crm_student_praise a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM crm_student_praise a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE crm_student_praise SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
